// years	y
// quarters	Q
// months	M
// weeks	w
// days	d
// hours	h
// minutes	m
// seconds	s
// milliseconds	ms

// 增加时间和减去时间
moment().add(7, 'days');
moment().subtract(7, 'days');

// 比较2个时间按照不同单位
// 第二个单位代表是否舍弃小数点,默认是false
a.diff(b) // 86400000 默认返回毫秒
a.diff(b, 'y', true); // 1.5 true则保留小数点
a.diff(b, 'y', false); // 1 false舍弃小数点

// 比较2个时间返回字符串
moment([2007, 0, 29]).fromNow();
moment([2007, 0, 29]).from(moment([2011,1,1]));
moment([2007, 0, 29]).toNow();
moment([2007, 0, 29]).to(moment([2011,1,1]));
