cells.data:
update({
	name: 'abc', // 查询条件
}, {
	$set: {
		age: 20, // 更新字段
	},
}, {
	multi: true,
	// 默认false
	// 为true则更新匹配到的所有文档
	upsert: true,
	// 默认false
	// 为true如果没有查询到文档
	// 则根据查询文档和更新文档创建一个文档,该文档的_id默认是objectId
});


return {
	ok: 1, // 操作成功的文档数量
	n: 1, // 查询到的文档数量
	nMoidfied: 1, //修改文档的数量
}
